pragma solidity ^0.8.0;

interface IReputationSystem {
    struct Position {
        uint256 stake;
        bool isYes;
        uint256 entryPrice;
        bool claimed;
    }

    event ReputationUpdated(
        address indexed user,
        uint256 oldReputation,
        uint256 newReputation,
        uint256 difficultyMultiplier
    );

    event MarketRecorded(
        address indexed market,
        address indexed user,
        uint256 stake,
        bool isYes,
        uint256 entryPrice
    );

    function whitelistFactory(address factory) external;

    function recordPosition(
        address market,
        address user,
        uint256 stake,
        bool isYes,
        uint256 currentPrice
    ) external;

    function updateReputation(
        address market,
        address user,
        bool outcome
    ) external returns (uint256 reputationChange);

    function calculateFeeDiscount(address user) external view returns (uint256);

    function calculateVotingWeight(address user) external view returns (uint256);

    function getUserReputation(address user) external view returns (uint256);

    function getUserStats(address user) external view returns (
        uint256 reputation,
        uint256 totalPredictions,
        uint256 correctPredictions
    );
}

contracts/Market.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./interfaces/IMarket.sol";
import "./interfaces/IReputationSystem.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title Market
 * @dev Implementation of a binary prediction market with reputation system
 */
contract Market is IMarket, ReentrancyGuard {
    using Math for uint256;

    // Constants for price calculations
    uint256 private constant SCALE = 1e18;
    uint256 private constant MIN_PRICE = SCALE / 100; // 0.01
    uint256 private constant MAX_PRICE = 99 * SCALE / 100; // 0.99
    uint256 private constant BASE_FEE = 2 * SCALE / 100; // 2% base fee

    // Market properties
    string public question;
    uint256 public resolutionTime;
    address public creator;
    IERC20 public collateralToken;
    MarketStatus public status;
    MarketOutcome public outcome;
    IReputationSystem public reputationSystem;

    // Liquidity pool state
    uint256 public totalYesShares;
    uint256 public totalNoShares;
    uint256 public liquidityPool;

    // User positions
    mapping(address => uint256) public yesBalances;
    mapping(address => uint256) public noBalances;

    // Claimed status tracking
    mapping(address => bool) public hasClaimed;

    // Modifiers
    modifier onlyBeforeResolution() {
        require(block.timestamp < resolutionTime, "Market: Too late");
        _;
    }

    modifier onlyAfterResolution() {
        require(block.timestamp >= resolutionTime, "Market: Too early");
        _;
    }

    modifier onlyTrading() {
        require(status == MarketStatus.TRADING, "Market: Not trading");
        _;
    }

    /**
     * @dev Initializes the market with given parameters
     */
    function initialize(
        string memory _question,
        uint256 _resolutionTime,
        address _creator,
        address _reputationSystem
    ) external override {
        require(bytes(question).length == 0, "Already initialized");
        require(_resolutionTime > block.timestamp, "Invalid resolution time");
        require(_reputationSystem != address(0), "Invalid reputation system");

        question = _question;
        resolutionTime = _resolutionTime;
        creator = _creator;
        status = MarketStatus.TRADING;
        reputationSystem = IReputationSystem(_reputationSystem);

        emit MarketCreated(address(this), _question, _resolutionTime, _creator);
    }

    /**
     * @dev Calculates effective fee rate after reputation discount
     */
    function calculateEffectiveFee(address user) public view returns (uint256) {
        uint256 discount = reputationSystem.calculateFeeDiscount(user);
        return BASE_FEE - ((BASE_FEE * discount) / SCALE);
    }

    /**
     * @dev Calculates the price for a given amount of shares
     */
    function calculatePrice(uint256 pool, uint256 shares) internal pure returns (uint256) {
        uint256 newPool = pool + shares;
        return (newPool * newPool - pool * pool) / (2 * SCALE);
    }

    /**
     * @dev Get current price of YES outcome
     */
    function getCurrentPrice() public view returns (uint256) {
        uint256 totalLiquidity = totalYesShares + totalNoShares;
        if (totalLiquidity == 0) return SCALE / 2; // 50% if no liquidity
        return (totalYesShares * SCALE) / totalLiquidity;
    }

    /**
     * @dev Buys outcome tokens
     */
    function buy(bool isYes, uint256 amount) 
        external 
        override 
        onlyTrading 
        onlyBeforeResolution 
        nonReentrant 
        returns (uint256)
    {
        require(amount > 0, "Amount must be positive");

        uint256 fee = (amount * calculateEffectiveFee(msg.sender)) / SCALE;
        uint256 effectiveAmount = amount - fee;

        uint256 shares;
        if (isYes) {
            shares = calculatePrice(totalYesShares, effectiveAmount);
            totalYesShares += shares;
            yesBalances[msg.sender] += shares;
        } else {
            shares = calculatePrice(totalNoShares, effectiveAmount);
            totalNoShares += shares;
            noBalances[msg.sender] += shares;
        }

        liquidityPool += effectiveAmount;
        require(collateralToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");

        // Record position for reputation
        reputationSystem.recordPosition(
            address(this),
            msg.sender,
            shares,
            isYes,
            getCurrentPrice()
        );

        emit Trade(msg.sender, isYes, amount, shares);
        return shares;
    }

    /**
     * @dev Sells outcome tokens
     */
    function sell(bool isYes, uint256 shares)
        external
        override
        onlyTrading
        onlyBeforeResolution
        nonReentrant
        returns (uint256)
    {
        require(shares > 0, "Shares must be positive");

        uint256 amount;
        if (isYes) {
            require(yesBalances[msg.sender] >= shares, "Insufficient yes shares");
            amount = calculatePrice(totalYesShares - shares, shares);
            totalYesShares -= shares;
            yesBalances[msg.sender] -= shares;
        } else {
            require(noBalances[msg.sender] >= shares, "Insufficient no shares");
            amount = calculatePrice(totalNoShares - shares, shares);
            totalNoShares -= shares;
            noBalances[msg.sender] -= shares;
        }

        uint256 fee = (amount * calculateEffectiveFee(msg.sender)) / SCALE;
        uint256 effectiveAmount = amount - fee;

        liquidityPool -= effectiveAmount;
        require(collateralToken.transfer(msg.sender, effectiveAmount), "Transfer failed");

        emit Trade(msg.sender, isYes, effectiveAmount, shares);
        return effectiveAmount;
    }

    /**
     * @dev Resolves the market with final outcome
     */
    function resolve(MarketOutcome _outcome)
        external
        override
        onlyAfterResolution
        nonReentrant
    {
        require(status == MarketStatus.TRADING, "Already resolved");
        require(_outcome != MarketOutcome.UNRESOLVED, "Invalid outcome");

        // Calculate voting weight if resolver is not creator
        if (msg.sender != creator) {
            uint256 weight = reputationSystem.calculateVotingWeight(msg.sender);
            require(weight >= 2 * SCALE, "Insufficient reputation to resolve");
        }

        status = MarketStatus.RESOLVED;
        outcome = _outcome;

        emit MarketResolved(address(this), _outcome, msg.sender);
    }

    /**
     * @dev Claims winnings after market resolution
     */
    function claim()
        external
        override
        nonReentrant
        returns (uint256)
    {
        require(status == MarketStatus.RESOLVED, "Not resolved");
        require(!hasClaimed[msg.sender], "Already claimed");

        uint256 amount;
        bool isWinner;

        if (outcome == MarketOutcome.YES) {
            amount = yesBalances[msg.sender];
            isWinner = true;
        } else if (outcome == MarketOutcome.NO) {
            amount = noBalances[msg.sender];
            isWinner = true;
        } else if (outcome == MarketOutcome.INVALID) {
            // Return proportional amount of liquidity pool for invalid outcome
            uint256 totalShares = yesBalances[msg.sender] + noBalances[msg.sender];
            amount = (totalShares * liquidityPool) / (totalYesShares + totalNoShares);
        }

        if (amount > 0) {
            // Update reputation if it was a valid outcome
            if (isWinner) {
                reputationSystem.updateReputation(
                    address(this),
                    msg.sender,
                    outcome == MarketOutcome.YES
                );
            }

            // Reset balances and mark as claimed
            yesBalances[msg.sender] = 0;
            noBalances[msg.sender] = 0;
            hasClaimed[msg.sender] = true;

            require(collateralToken.transfer(msg.sender, amount), "Transfer failed");
        }

        return amount;
    }

    /**
     * @dev View functions for market information
     */
    function getMarketInfo() external view returns (
        string memory _question,
        uint256 _resolutionTime,
        address _creator,
        MarketStatus _status,
        MarketOutcome _outcome,
        uint256 _currentPrice,
        uint256 _liquidityPool
    ) {
        return (
            question,
            resolutionTime,
            creator,
            status,
            outcome,
            getCurrentPrice(),
            liquidityPool
        );
    }

    /**
     * @dev View function for user position
     */
    function getUserPosition(address user) external view returns (
        uint256 yesShares,
        uint256 noShares,
        bool claimed
    ) {
        return (
            yesBalances[user],
            noBalances[user],
            hasClaimed[user]
        );
    }
}

contracts/MarketFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./interfaces/IMarket.sol";
import "./interfaces/IReputationSystem.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title MarketFactory
 * @dev Factory contract for creating and tracking prediction markets
 */
contract MarketFactory is Ownable, ReentrancyGuard {
    using Clones for address;

    // State variables
    address public implementation;
    address public reputationSystem;
    uint256 public marketCount;

    // Enhanced market tracking with more efficient storage
    mapping(uint256 => address) public marketById;
    mapping(address => uint256) public marketIds;

    // Events
    event ImplementationUpdated(
        address indexed oldImpl, 
        address indexed newImpl,
        uint256 timestamp
    );

    event ReputationSystemUpdated(
        address indexed oldSystem,
        address indexed newSystem,
        uint256 timestamp
    );

    event MarketCreated(
        uint256 indexed marketId,
        address indexed market,
        string question,
        uint256 resolutionTime,
        address indexed creator,
        uint256 timestamp
    );

    // Custom errors
    error InvalidImplementation();
    error InvalidReputationSystem();
    error InvalidRange();
    error RangeOutOfBounds();
    error MarketInitializationFailed();

    /**
     * @dev Constructor sets the implementation contract and reputation system
     */
    constructor(
        address initialOwner,
        address _implementation, 
        address _reputationSystem
    ) Ownable(initialOwner) {
        if (_implementation == address(0)) revert InvalidImplementation();
        if (_reputationSystem == address(0)) revert InvalidReputationSystem();

        implementation = _implementation;
        reputationSystem = _reputationSystem;
    }

    /**
     * @dev Updates the implementation contract
     */
    function setImplementation(address _implementation) external onlyOwner {
        if (_implementation == address(0)) revert InvalidImplementation();

        address oldImpl = implementation;
        implementation = _implementation;

        emit ImplementationUpdated(
            oldImpl, 
            _implementation,
            block.timestamp
        );
    }

    /**
     * @dev Updates the reputation system contract
     */
    function setReputationSystem(address _reputationSystem) external onlyOwner {
        if (_reputationSystem == address(0)) revert InvalidReputationSystem();

        address oldSystem = reputationSystem;
        reputationSystem = _reputationSystem;

        emit ReputationSystemUpdated(
            oldSystem,
            _reputationSystem,
            block.timestamp
        );
    }

    /**
     * @dev Creates a new prediction market
     */
    function createMarket(
        string calldata question,
        uint256 resolutionTime
    ) external nonReentrant returns (address market) {
        // Increment market count first to avoid reentrancy
        uint256 newMarketId = ++marketCount;

        // Clone the implementation contract
        market = implementation.clone();

        // Initialize the market with try-catch
        try IMarket(market).initialize(
            question,
            resolutionTime,
            msg.sender,
            reputationSystem
        ) {
            // Track the new market
            marketById[newMarketId] = market;
            marketIds[market] = newMarketId;

            // Whitelist the market in reputation system
            IReputationSystem(reputationSystem).whitelistFactory(address(this));

            emit MarketCreated(
                newMarketId,
                market,
                question,
                resolutionTime,
                msg.sender,
                block.timestamp
            );

            return market;
        } catch {
            revert MarketInitializationFailed();
        }
    }

    /**
     * @dev Returns a list of markets within the given range
     */
    function getMarkets(uint256 start, uint256 end) 
        external 
        view 
        returns (address[] memory result) 
    {
        if (start >= end) revert InvalidRange();
        if (end > marketCount) revert RangeOutOfBounds();

        result = new address[](end - start);
        for (uint256 i = start; i < end; i++) {
            result[i - start] = marketById[i + 1];
        }
    }

    /**
     * @dev Checks if an address is a market created by this factory
     */
    function isMarket(address market) external view returns (bool) {
        return marketIds[market] != 0;
    }
}

contracts/ReputationSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./interfaces/IReputationSystem.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title ReputationSystem
 * @dev Implementation of reputation scoring and difficulty multiplier system
 */
contract ReputationSystem is IReputationSystem, Ownable, ReentrancyGuard {
    using Math for uint256;

    uint256 private constant SCALE = 1e18;
    uint256 private constant BASE_REPUTATION_REWARD = 100 * SCALE;
    uint256 private constant MAX_DIFFICULTY_MULTIPLIER = 5 * SCALE;
    uint256 private constant MIN_DIFFICULTY_MULTIPLIER = SCALE;
    uint256 private constant MAX_DISCOUNT = 50 * SCALE / 100; // 50%
    uint256 private constant MAX_VOTING_WEIGHT = 3 * SCALE;   // 3x
    uint256 private constant REPUTATION_THRESHOLD = 1000000 * SCALE;

    mapping(address => uint256) private userReputation;
    mapping(address => uint256) private totalPredictions;
    mapping(address => uint256) private correctPredictions;
    // Track consensus for each market
    mapping(address => MarketConsensus) private marketConsensus;
    // Whitelist of market factory contracts that can interact
    mapping(address => bool) public whitelistedFactories;

    constructor(address initialOwner) Ownable(initialOwner) {}

    // Market consensus tracking
    struct MarketConsensus {
        uint256 finalPrice;
        uint256 totalStaked;
        bool resolved;
        mapping(address => Position) userPositions;
    }

    // Modifiers
    modifier onlyWhitelistedFactory() {
        require(whitelistedFactories[msg.sender], "Not authorized");
        _;
    }

    /**
     * @dev Whitelist a market factory contract
     */
    function whitelistFactory(address factory) external onlyOwner {
        whitelistedFactories[factory] = true;
    }

    /**
     * @dev Remove a market factory from whitelist
     */
    function removeFactory(address factory) external onlyOwner {
        whitelistedFactories[factory] = false;
    }

    /**
     * @dev Records a user's position in a market
     */
    function recordPosition(
        address market,
        address user,
        uint256 stake,
        bool isYes,
        uint256 currentPrice
    ) external override onlyWhitelistedFactory {
        MarketConsensus storage consensus = marketConsensus[market];
        require(!consensus.resolved, "Market already resolved");

        Position storage position = consensus.userPositions[user];
        require(position.stake == 0, "Position already recorded");

        position.stake = stake;
        position.isYes = isYes;
        position.entryPrice = currentPrice;
        position.claimed = false;

        consensus.totalStaked += stake;

        emit MarketRecorded(market, user, stake, isYes, currentPrice);
    }

    /**
     * @dev Calculate difficulty multiplier based on price deviation
     */
    function calculateDifficultyMultiplier(
        uint256 finalPrice,
        bool outcome
    ) public pure returns (uint256) {
        uint256 deviation;
        if (outcome) {
            // YES outcome
            deviation = SCALE - finalPrice;
        } else {
            // NO outcome
            deviation = finalPrice;
        }

        return MIN_DIFFICULTY_MULTIPLIER + 
            (deviation * (MAX_DIFFICULTY_MULTIPLIER - MIN_DIFFICULTY_MULTIPLIER)) / SCALE;
    }

    /**
     * @dev Updates user reputation after market resolution
     */
    function updateReputation(
        address market,
        address user,
        bool outcome
    ) external override onlyWhitelistedFactory nonReentrant returns (uint256) {
        MarketConsensus storage consensus = marketConsensus[market];
        require(!consensus.resolved, "Already resolved");

        Position storage position = consensus.userPositions[user];
        require(!position.claimed, "Already claimed");
        require(position.stake > 0, "No position");

        consensus.resolved = true;
        consensus.finalPrice = position.entryPrice;
        position.claimed = true;

        uint256 multiplier = calculateDifficultyMultiplier(
            consensus.finalPrice,
            outcome
        );

        totalPredictions[user]++;

        uint256 reputationChange = 0;
        if (position.isYes == outcome) {
            correctPredictions[user]++;

            uint256 stakeWeight = (position.stake * SCALE) / consensus.totalStaked;
            reputationChange = (BASE_REPUTATION_REWARD * multiplier * stakeWeight) / (SCALE * SCALE);

            uint256 oldReputation = userReputation[user];
            userReputation[user] += reputationChange;

            emit ReputationUpdated(
                user,
                oldReputation,
                userReputation[user],
                multiplier
            );
        }

        return reputationChange;
    }

    /**
     * @dev Calculate fee discount based on reputation
     */
    function calculateFeeDiscount(
        address user
    ) external view override returns (uint256) {
        uint256 reputation = userReputation[user];

        if (reputation >= REPUTATION_THRESHOLD) {
            return MAX_DISCOUNT;
        }

        return (reputation * MAX_DISCOUNT) / REPUTATION_THRESHOLD;
    }

    /**
     * @dev Calculate voting weight based on reputation
     */
    function calculateVotingWeight(
        address user
    ) external view override returns (uint256) {
        uint256 reputation = userReputation[user];

        if (reputation >= REPUTATION_THRESHOLD) {
            return MAX_VOTING_WEIGHT;
        }

        return SCALE + ((reputation * (MAX_VOTING_WEIGHT - SCALE)) / REPUTATION_THRESHOLD);
    }

    /**
     * @dev Get user's current reputation
     */
    function getUserReputation(
        address user
    ) external view override returns (uint256) {
        return userReputation[user];
    }

    /**
     * @dev Get user's complete stats
     */
    function getUserStats(
        address user
    ) external view override returns (
        uint256 reputation,
        uint256 predictions,
        uint256 correct
    ) {
        return (
            userReputation[user],
            totalPredictions[user],
            correctPredictions[user]
        );
    }
}

contracts/YourContract.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

// Useful for debugging. Remove when deploying to a live network.
import "hardhat/console.sol";

// Use openzeppelin to inherit battle-tested implementations (ERC20, ERC721, etc)
// import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * A smart contract that allows changing a state variable of the contract and tracking the changes
 * It also allows the owner to withdraw the Ether in the contract
 * @author BuidlGuidl
 */
contract YourContract {
    // State Variables
    address public immutable owner;
    string public greeting = "Building Unstoppable Apps!!!";
    bool public premium = false;
    uint256 public totalCounter = 0;
    mapping(address => uint) public userGreetingCounter;

    // Events: a way to emit log statements from smart contract that can be listened to by external parties
    event GreetingChange(address indexed greetingSetter, string newGreeting, bool premium, uint256 value);

    // Constructor: Called once on contract deployment
    // Check packages/hardhat/deploy/00_deploy_your_contract.ts
    constructor(address _owner) {
        owner = _owner;
    }

    // Modifier: used to define a set of rules that must be met before or after a function is executed
    // Check the withdraw() function
    modifier isOwner() {
        // msg.sender: predefined variable that represents address of the account that called the current function
        require(msg.sender == owner, "Not the Owner");
        _;
    }

    /**
     * Function that allows anyone to change the state variable "greeting" of the contract and increase the counters
     *
     * @param _newGreeting (string memory) - new greeting to save on the contract
     */
    function setGreeting(string memory _newGreeting) public payable {
        // Print data to the hardhat chain console. Remove when deploying to a live network.
        console.log("Setting new greeting '%s' from %s", _newGreeting, msg.sender);

        // Change state variables
        greeting = _newGreeting;
        totalCounter += 1;
        userGreetingCounter[msg.sender] += 1;

        // msg.value: built-in global variable that represents the amount of ether sent with the transaction
        if (msg.value > 0) {
            premium = true;
        } else {
            premium = false;
        }

        // emit: keyword used to trigger an event
        emit GreetingChange(msg.sender, _newGreeting, msg.value > 0, msg.value);
    }

    /**
     * Function that allows the owner to withdraw all the Ether in the contract
     * The function can only be called by the owner of the contract as defined by the isOwner modifier
     */
    function withdraw() public isOwner {
        (bool success, ) = owner.call{ value: address(this).balance }("");
        require(success, "Failed to send Ether");
    }

    /**
     * Function that allows the contract to receive ETH
     */
    receive() external payable {}
}

